alter Procedure [dbo].[StpXXXLaravelSeed] ( @TableName As VarChar(50), @Kondisi As VarChar(100) )WITH EXECUTE as CALLER -- , ENCRYPTIONASSet NoCount On;/*Select * From TBLMNUSelect * From INFORMATION_SCHEMA.COLUMNS Where TABLE_NAME = 'TBLMNU'SELECT ',' + COLUMN_NAME From INFORMATION_SCHEMA.COLUMNS Where TABLE_NAME = 'TBLMNU'              FOR XML PATH ('')*/Declare @SQL As NVarChar(Max)--Set @TableName = 'TBLMNU'SELECT @SQL = STUFF((            SELECT                 Case When DATA_TYPE = 'datetime' Then                         ', ''''' + COLUMN_NAME + ''''' =>  Date("Y-m-d H:i:s") '                     Else                         ', ''''' + COLUMN_NAME + ''''' => '''''' + IsNull(Cast(RTRIM(' + COLUMN_NAME + ') As VarChar),'''')  + '''''' '                     END            From INFORMATION_SCHEMA.COLUMNS             Where TABLE_NAME = @TableName            FOR XML PATH ('')            ), 1, 1, '')--Set @SQL = Replace(@SQL,'''','''''')Set @SQL = Replace(@SQL,'&gt;','>')Set @SQL = 'Select '' DB::table('''''+@TableName+''''')->insert([ ' + @SQL + ']); ''  From ' + @TableName + @KondisiSelect @SQLEXEC Sp_ExecuteSql @SQL; /*Exec StpXXXLaravelSeed 'TBLUSR', ' Where TUUSER = ''Admin'' 'Exec StpXXXLaravelSeed 'TBLMNU', ''Exec StpXXXLaravelSeed 'TBLSYS', ' Where TSDSCD In (''DSPLY'',''MODE'') ' DB::table('TBLMNU')->insert([  'TMMENUIY' => '1' , 'TMNOMR' => '01' , 'TMGRUP' => '' , 'TMMENU' => 'FILE' , 'TMDESC' => '' , 'TMSCUT' => '' , 'TMACES' => '' , 'TMBCDT' => '99' , 'TMFWDT' => '99' , 'TMURLW' => '' , 'TMSYFG' => 'E' , 'TMUSCT' => '0' , 'TMLSDT' =>  Date("Y-m-d H:i:s") , 'TMLSBY' => '' , 'TMRLDT' => '20161215' , 'TMGRID' => '' , 'TMREMK' => '' , 'TMRGID' => 'APPSERVER06\Administrator' , 'TMRGDT' =>  Date("Y-m-d H:i:s") , 'TMCHID' => 'APPSERVER06\Administrator' , 'TMCHDT' =>  Date("Y-m-d H:i:s") , 'TMCHNO' => '0' , 'TMDLFG' => '0' , 'TMDPFG' => '1' , 'TMPTFG' => '0' , 'TMPTCT' => '0' , 'TMPTID' => '' , 'TMPTDT' =>  Date("Y-m-d H:i:s") , 'TMSRCE' => '' , 'TMUSRM' => '' , 'TMITRM' => '' , 'TMCSDT' =>  Date("Y-m-d H:i:s") , 'TMCSID' => 'APPSERVER06Administrator' ]);  Select     'DB::table(''TBLMNU'')->insert([   ' +    '''TMMENUIY'' => ''' + Cast(RTRIM(IsNull(TMMENUIY,'')) As VarChar) + '''' +' , ''TMNOMR'' => ''' + Cast(RTRIM(IsNull(TMNOMR,'')) As VarChar) + '''' +' , ''TMGRUP'' => ''' + Cast(RTRIM(IsNull(TMGRUP,'')) As VarChar) + '''' +' , ''TMMENU'' => ''' + Cast(RTRIM(IsNull(TMMENU,'')) As VarChar) + '''' +' , ''TMDESC'' => ''' + Cast(RTRIM(IsNull(TMDESC,'')) As VarChar) + '''' +' , ''TMSCUT'' => ''' + Cast(RTRIM(IsNull(TMSCUT,'')) As VarChar) + '''' +' , ''TMACES'' => ''' + Cast(RTRIM(IsNull(TMACES,'')) As VarChar) + '''' +' , ''TMBCDT'' => ''' + Cast(RTRIM(IsNull(TMBCDT,'')) As VarChar) + '''' +' , ''TMFWDT'' => ''' + Cast(RTRIM(IsNull(TMFWDT,'')) As VarChar) + '''' +' , ''TMURLW'' => ''' + Cast(RTRIM(IsNull(TMURLW,'')) As VarChar) + '''' +' , ''TMSYFG'' => ''' + Cast(RTRIM(IsNull(TMSYFG,'')) As VarChar) + '''' +--' , ''TMUSCT'' => ''' + Cast(RTRIM(IsNull(TMUSCT,'')) As VarChar) + '''' +--' , ''TMLSDT'' => ''' + Cast(RTRIM(IsNull(TMLSDT,'')) As VarChar) + '''' +--' , ''TMLSBY'' => ''' + Cast(RTRIM(IsNull(TMLSBY,'')) As VarChar) + '''' +--' , ''TMRLDT'' => ''' + Cast(RTRIM(IsNull(TMRLDT,'')) As VarChar) + '''' +' , ''TMGRID'' => ''SysAdmin'' ' + ' , ''TMREMK'' => ''' + Cast(RTRIM(IsNull(TMREMK,'')) As VarChar) + '''' +--' , ''TMRGID'' => ''' + Cast(RTRIM(IsNull(TMRGID,'')) As VarChar) + '''' +' , ''TMRGDT'' => Date("Y-m-d H:i:s") ' + ' , ''TMCHID'' => ''SysAdmin'' ' + ' , ''TMCHDT'' => Date("Y-m-d H:i:s") ' + ' , ''TMCHNO'' => 0 ' + ' , ''TMDLFG'' => ''' + Cast(RTRIM(IsNull(TMDLFG,'')) As VarChar) + '''' +' , ''TMDPFG'' => ''' + Cast(RTRIM(IsNull(TMDPFG,'')) As VarChar) + '''' +--' , ''TMPTFG'' => ''' + Cast(RTRIM(IsNull(TMPTFG,'')) As VarChar) + '''' +--' , ''TMPTCT'' => ''' + Cast(RTRIM(IsNull(TMPTCT,'')) As VarChar) + '''' +--' , ''TMPTID'' => '''